import cx from 'classnames';
import { Component, Prop } from 'vue-property-decorator';
import { BaseInput } from './BaseInput';
import { ICaptureSourceMetadata } from './index';
import { IListMetadata, IWindowsListOption } from 'components/shared/inputs';
import { Multiselect } from 'vue-multiselect';
import { createProps } from 'components/tsx-component';
import styles from './CaptureInput.m.less';
import { $t } from 'services/i18n';

const { desktopCapturer, nativeImage} = require('electron')
const screen = require('electron').remote.screen
interface IOptionData {
  bgColor?: string;
}
class Props {
  handleOnSearch?: (str: string) => unknown = () => null;
  handleOnSelect?: (option: IWindowsListOption<string, unknown>) => unknown = () => null;
}

@Component({ props: createProps(Props) })
export default class CaptureInput extends BaseInput<string, ICaptureSourceMetadata<string, IOptionData>, Props> {
  @Prop() readonly value: string;
  @Prop({ default: () => ({}) }) readonly metadata: ICaptureSourceMetadata<string>;
  @Prop() readonly title: string;

  isLoading = true;
  optionsUpdated = false;
  localoptions : IWindowsListOption<string, IOptionData>[];
  
  pickerVisible = false;

  private onInputHandler(values: IWindowsListOption<string>) {
    console.log("onInputHandler in caputureinput " + values.value );
    this.emitInput(values.value);
  }

  asyncFind() {
    console.log("asyncFind called ");
    if (this.optionsUpdated)
      return;

    this.isLoading = true;

    let localoptions = [
      { value: 'game:1', title: "Auto Game", thumbnail: "data:image/png;base64," }, 
      { value: 'game:2', title: "Fullscreen Game", thumbnail:"data:image/png;base64," }, 
    ];

    desktopCapturer.getSources({ types: ['window'] }).then(sources => {
       for (const source of sources) {
        localoptions.push({ 'title': source.name.substring(0, 24), 'value': source.id, 'thumbnail': source.thumbnail.toDataURL() });
      }
    }) 

    const displays =  screen.getAllDisplays();
    let display_id = 0;
    console.log("display count " + displays.length);
    for (const display of displays) {
      localoptions.push({ 'title': "Display "+display.size, 'value': "monitor:"+display_id, 'thumbnail': "data:image/png;base64," });
      display_id++;
    }
    
    this.localoptions = localoptions;
    this.isLoading = false;
    this.optionsUpdated = true;
  }
  

  togglePicker() {
    this.pickerVisible = !this.pickerVisible;
  }
  
  get pickerBody() {
    return (
      <transition name="capturesource-slide">
        {this.pickerVisible && (
          <div
            id='capture-sources'
            class={cx(styles.capturesourceContainer, {})}
          >
            <Multiselect
              style='width: 100%'
              value={this.value}
              label="title"
              trackBy="value"
              options={this.localoptions}
              multiple={false}
              loading={this.isLoading}
              taggable={false}
              closeOnSelect={false}
              searchable={false}
              allowEmpty={true}
              showPreview={false}
              showText={false}
              search-change={this.asyncFind()}
              onInput={(tags: IWindowsListOption) => this.onInputHandler(tags)}
              
              scopedSlots={{
                option: (props: { option: IWindowsListOption<IOptionData> }) => (
                  <div>
                  <img src={props.option.thumbnail} ></img>
                  <div data-option-title={props.option.title} data-option-value={props.option.value}>
                    {this.$scopedSlots.item ? this.$scopedSlots.item(props) : props.option.title}
                  </div>
                  </div>
              ),
              }}
            />
          </div>
        )}
      </transition>
    );
  }


  render() {
    return (
      <div
        data-role="input"
        data-type="capture"
        data-name={this.options.name}
        class="input-wrapper"
        style='width: 100%'
      >
        <div class={styles.capturesource}>
          <div class={styles.capturesourceText} onClick={() => this.togglePicker()}>
            <input
              class={styles.capturesourceInput}
              name="capturesource-input"
              type="text"
              readonly
              value={this.value}
            />
          </div>
          {this.pickerBody}
        </div>
      </div>
    );
  }
}
